mapper.select:
SELECT phone 
FROM demoDb.member 
WHERE num = #{memberModel}

mapper.insert:
INSERT INTO demoDb.TBL_USER (
	USER_ID
	, USER_PW
	, USER_NAME
	, USER_EMAIL
) VALUES (
	#{userId}
	, #{userPw}
	, #{userName}
	, #{userEmail}
)

mapper.select:
SELECT
	TU.USER_NAME
FROM demoDb.TBL_USER TU
WHERE 1=1
AND TU.USER_ID = #{userId}
AND TU.USER_PW = #{userPw}
